    return (
    //   <div onChange={this.onChangeValue}>
    <div>
        <Form>
            <input
                type="radio" 
                value="videographer" 
                name="type" 
                onChange={(e) => setSearchQuery(e.target.value)}
            /> Videographer
            <input 
                type="radio"
                value="photographer"
                name="type"
                onChange={(e) => setSearchQuery(e.target.value)}
            /> Photographer
            <input
                type="radio" 
                value="_events" 
                name="type" 
                onChange={(e) => setSearchQuery(e.target.value)}
            /> Events
            <input 
                type="radio"
                value="_retail"
                name="type"
                onChange={(e) => setSearchQuery(e.target.value)}
            /> Retail
            <input 
                type="radio"
                value="_hospitality"
                name="type"
                onChange={(e) => setSearchQuery(e.target.value)}
            /> Hospitality
            <input 
                type="radio"
                value="_corporate"
                name="type"
                onChange={(e) => setSearchQuery(e.target.value)}
            /> Corporate
        </Form>
    </div>



    );
  }

export default Filters;
