import React from "react";
import { Container } from 'react-bootstrap';
import freelancer_info from "../data/freelancer_data";
import FreelancerList from '../containers/FreelancerList.js';
import FreelancerHeader from '../containers/FreelancerHeader.js';
import Footer from '../containers/Footer.js';
import SearchBar from '../containers/SearchBar.js';
import './Freelancers.css';
import { useState } from 'react';


function Freelancers() {
  const { search } = window.location;
  const query = new URLSearchParams(search).get('s');
  const [searchQuery, setSearchQuery] = useState(query || '');
  const filteredFreelancers = filterFreelancers(freelancer_info, searchQuery);

  const filterFreelancers = (freelancer_info, query) => {
    if (!query) {
        return freelancer_info;
    }

    return freelancer_info.filter((freelancer) => {
        // const freelancerTest = <FreelancerCard
        //                           key={freelancer.id}
        //                           freelancer={freelancer} />
        const freelancerName = freelancer.name.toLowerCase();
        return freelancerName.includes(query);
    });
  };


  class Freelancers extends React.Component {
    constructor(props) {
      super(props);

      this.state = {
        freelancer_info: freelancer_info.slice(0, 20)
      };
    }

    render() {
        console.log(this.state);
    
        return (

          <Container id="content">
            <FreelancerHeader />
            <SearchBar 
              searchQuery={searchQuery}
              setSearchQuery={setSearchQuery}/>
            <FreelancerList freelancer_info = {this.state.filterFreelancers} />
              {/* // freelancer_info={this.state.freelancer_info} /> */}
            <Footer />
          </Container>
        );
      }
  }
  
}
export default Freelancers;


